package edu.mayo.dhs.ievaluate.api.applications;

import java.util.Date;
import java.util.List;
import java.util.UUID;

/**
 * Represents a cohort of assertions generated by an application
 */
public abstract class ApplicationAssertionCollection {
    protected UUID id;
    protected ProfiledApplication application;
    protected String applicationVersion;
    protected Date assertionGenerateDTM;
    protected List<ApplicationAssertion> items;

    public UUID getId() {
        return id;
    }

    public void setId(UUID id) {
        this.id = id;
    }

    public ProfiledApplication getApplication() {
        return application;
    }

    public void setApplication(ProfiledApplication application) {
        this.application = application;
    }

    public String getApplicationVersion() {
        return applicationVersion;
    }

    public void setApplicationVersion(String applicationVersion) {
        this.applicationVersion = applicationVersion;
    }

    public Date getAssertionGenerateDTM() {
        return assertionGenerateDTM;
    }

    public void setAssertionGenerateDTM(Date assertionGenerateDTM) {
        this.assertionGenerateDTM = assertionGenerateDTM;
    }

    public List<ApplicationAssertion> getItems() {
        return items;
    }

    public void setItems(List<ApplicationAssertion> items) {
        this.items = items;
    }
}